");        }               $sn = 0;        foreach($data as $tb_name){                               if($tb_name->Status!=="Requested"){                $sn +=  1;         ?>        <tr>           <td><?=$sn?></td>           <td><?=$tb_name->Title?></td>           <td><?=$tb_name->Location?></td>           <td><?=$tb_name->Type?></td>           <td><?=$tb_name->Status?></td>           <td><?=$tb_name->Purpose?></td>           <td><?=$tb_name->Upload_time?></td>           <td><?=$tb_name->Update_time?></td>           <td><a href="index.php?file=post&&sub_file=post-new&&Sn=<?=$tb_name->Sn?>" class="fa fa-edit"></a> <a href="delete.php?Sn=<?=$tb_name->Sn?>&&delete=post_information&&link=post" class="fa fa-trash"></a></td>        </tr>        <?php } } ?>            </table></div>